tinymce.PluginManager.add('videothumbnail', function(editor, url){
	editor.addButton('videothumbnail', {
		text: 'Добавить видео',
		icon: false,
		onclick: function(){
			editor.windowManager.open({
				title: 'Видео (с YouTube.com или Vimeo.com)',
				body: [
					{type: 'textbox', name: 'url', label: 'Ссылка'}
				],
				onsubmit: function(e){
					$('#modal2').trigger('openModal');
					var video_url = e.data.url;
					$.ajax({
						type: "GET",
						url:'/generate_thumbnail/',
						data: {'url': video_url},
						dataType: 'json',
						success: function(data){
							var code = data[0]
							if(code == '200'){
								var html = '<a target="_blank" href="' + video_url + '"><img src="' + data[1] + '"></a>';
								editor.insertContent(html);
							}
							else{
								alert('Error');
							}
							$('#modal2').trigger('closeModal');
						}
					});
					
				}
			})
		}
	});

	editor.addMenuItem('videothumbnail', {
        text: 'Example plugin',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'TinyMCE site',
                url: 'http://www.tinymce.com',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});